mapper.select:
SELECT s.Section_Number from section_lecture l
join section s where
s.Section_ID = l.Section_ID and LECTURE_ID = #{id}

mapper.update:
UPDATE
lecture
SET Classroom_ID= #{givenRoomId}
where Lecture_ID= #{class_id}

UPDATE
lecture_time
SET Day_ID= #{givenDay},
Start= #{givenStart},
End=
#{givenEnd}
where LECTURE_ID= #{class_id}
AND Day_Id=#{req_day}
